mapper.select:

select 

from sfxie_sys_action
where id_ = #{id,jdbcType=VARCHAR}
  and partition_company = #{partitionCompany,jdbcType=VARCHAR}

mapper.delete:

delete from sfxie_sys_action
where id_ = #{id,jdbcType=VARCHAR}
  and partition_company = #{partitionCompany,jdbcType=VARCHAR}

mapper.insert:

insert into sfxie_sys_action (id_, action_name, action_url, 
  sequence_no, create_time, create_user, 
  update_time, update_user, is_valid, 
  param_flag, menu_code, partition_company
  )
values (#{id,jdbcType=VARCHAR}, #{actionName,jdbcType=VARCHAR}, #{actionUrl,jdbcType=VARCHAR}, 
  #{sequenceNo,jdbcType=DECIMAL}, #{createTime,jdbcType=TIMESTAMP}, #{createUser,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}, #{isValid,jdbcType=CHAR}, 
  #{paramFlag,jdbcType=VARCHAR}, #{menuCode,jdbcType=VARCHAR}, #{partitionCompany,jdbcType=VARCHAR}
  )
